import re
def find_capitalized_words(text):
    text = re.sub(r'\s+', ' ', text.strip())
    sentences = re.split(r'(?<=[.!?])\s+', text)
    result = []
    for sentence in sentences:
        if not sentence:
            continue
        words = sentence.split()
        if len(words) == 0:
            continue
        for word in words[1:]:
            if (word and word[0].isupper() and
                    any(c.isalpha() for c in word)):
                clean_word = re.sub(r'[.,!?;:]$', '', word)
                if clean_word and clean_word[0].isupper():
                    if len(clean_word) > 1:
                        result.append(clean_word)

    return result
def find_keywords_advanced(text, keywords, case_sensitive=False):
    found_keywords = []
    if not case_sensitive:
        text = text.lower()
        keywords = {k.lower() for k in keywords}
    import re
    words = re.findall(r'\b\w+\b', text)

    for word in words:
        word_to_check = word if case_sensitive else word.lower()
        if word_to_check in keywords and word_to_check not in found_keywords:
            found_keywords.append(word_to_check)

    return found_keywords

